describe('Welcome', function() {

  var inputYourName = element(by.input('yourName'));

  beforeEach(function()
  {
    browser.get('/');
  });

  it('Checar o nome inicial', function() {
    expect(element(by.binding('yourName')).getText()).toBe("Hello !");
  });

it('Checar o nome inicial com entrada Daniel', function() {
  inputYourName.sendKeys('Daniel');
  expect(element(by.binding('yourName')).getText()).toBe("Hello Daniel!");
});

it('Checar limite de campo', function() {
  inputYourName.sendKeys('');
  expect(element(by.binding('yourName')).getText()).toBe("Hello !");
});
  
});